import React, { useState, useEffect } from "react";
import DogadajDataService from "../services/Dogadajiservice";

const Dogadaj = props => {
  const initialDogadajitate = {
    id: null,
    title: "",
    description: "",
    published: false
  };
  const [currentDogadaj, setCurrentDogadaj] = useState(initialDogadajitate);
  const [message, setMessage] = useState("");

  const getDogadaj = id => {
    DogadajDataService.get(id)
      .then(response => {
        setCurrentDogadaj(response.data);
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  };

  useEffect(() => {
    getDogadaj(props.match.params.id);
  }, [props.match.params.id]);

  const handleInputChange = event => {
    const { name, value } = event.target;
    setCurrentDogadaj({ ...currentDogadaj, [name]: value });
  };

  const updatePublished = status => {
    var data = {
      id: currentDogadaj.id,
      title: currentDogadaj.title,
      description: currentDogadaj.description,
      published: status
    };

    DogadajDataService.update(currentDogadaj.id, data)
      .then(response => {
        setCurrentDogadaj({ ...currentDogadaj, published: status });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  };

  const updateDogadaj = () => {
    DogadajDataService.update(currentDogadaj.id, currentDogadaj)
      .then(response => {
        console.log(response.data);
        setMessage("The Dogadaj was updated successfully!");
      })
      .catch(e => {
        console.log(e);
      });
  };

  const deleteDogadaj = () => {
    DogadajDataService.remove(currentDogadaj.id)
      .then(response => {
        console.log(response.data);
        props.history.push("/Dogadaji");
      })
      .catch(e => {
        console.log(e);
      });
  };

  return (
    <div>
      {currentDogadaj ? (
        <div className="edit-form">
          <h4>Događaj</h4>
          <form>
            <div className="form-group">
              <label htmlFor="title">Naziv</label>
              <input
                type="text"
                className="form-control"
                id="title"
                name="title"
                value={currentDogadaj.title}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="description">Opis</label>
              <input
                type="text"
                className="form-control"
                id="description"
                name="description"
                value={currentDogadaj.description}
                onChange={handleInputChange}
              />
            </div>

            <div className="form-group">
              <label>
                <strong>Status:</strong>
              </label>
              {currentDogadaj.published ? "Published" : "Pending"}
            </div>
          </form>

          {currentDogadaj.published ? (
            <button
              className="badge badge-primary mr-2"
              onClick={() => updatePublished(false)}
            >
              UnPublish
            </button>
          ) : (
            <button
              className="badge badge-primary mr-2"
              onClick={() => updatePublished(true)}
            >
              Publish
            </button>
          )}

          <button className="badge badge-danger mr-2" onClick={deleteDogadaj}>
            Obriši
          </button>

          <button
            type="submit"
            className="badge badge-success"
            onClick={updateDogadaj}
          >
            Ažuriraj
          </button>
          <p>{message}</p>
        </div>
      ) : (
        <div>
          <br />
          <p>Klikni na odabrani događaj...</p>
        </div>
      )}
    </div>
  );
};

export default Dogadaj;
