import React, { useState, useEffect } from "react";
import DogadajDataService from "../services/DogadajService";
import { Link } from "react-router-dom";

const DogadajiList = () => {
  const [Dogadaji, setDogadaji] = useState([]);
  const [currentDogadaj, setCurrentDogadaj] = useState(null);
  const [currentIndex, setCurrentIndex] = useState(-1);
  const [searchTitle, setSearchTitle] = useState("");

  useEffect(() => {
    retrieveDogadaji();
  }, []);

  const onChangeSearchTitle = e => {
    const searchTitle = e.target.value;
    setSearchTitle(searchTitle);
  };

  const retrieveDogadaji = () => {
    DogadajDataService.getAll()
      .then(response => {
        setDogadaji(response.data);
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  };

  const refreshList = () => {
    retrieveDogadaji();
    setCurrentDogadaj(null);
    setCurrentIndex(-1);
  };

  const setActiveDogadaj = (Dogadaj, index) => {
    setCurrentDogadaj(Dogadaj);
    setCurrentIndex(index);
  };

  const removeAllDogadaji = () => {
    DogadajDataService.removeAll()
      .then(response => {
        console.log(response.data);
        refreshList();
      })
      .catch(e => {
        console.log(e);
      });
  };

  const findByTitle = () => {
    DogadajDataService.findByTitle(searchTitle)
      .then(response => {
        setDogadaji(response.data);
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  };

  return (
    <div className="list row">
      <div className="col-md-8">
        <div className="input-group mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="Pretraži po imenu"
            value={searchTitle}
            onChange={onChangeSearchTitle}
          />
          <div className="input-group-append">
            <button
              className="btn btn-outline-secondary"
              type="button"
              onClick={findByTitle}
            >
              Pretraži
            </button>
          </div>
        </div>
      </div>
      <div className="col-md-6">
        <h4>Lista događaja</h4>

        <ul className="list-group">
          {Dogadaji &&
            Dogadaji.map((Dogadaj, index) => (
              <li
                className={
                  "list-group-item " + (index === currentIndex ? "active" : "")
                }
                onClick={() => setActiveDogadaj(Dogadaj, index)}
                key={index}
              >
                {Dogadaj.title}
              </li>
            ))}
        </ul>

        <button
          className="m-3 btn btn-sm btn-danger"
          onClick={removeAllDogadaji}
        >
          Ukloni sve
        </button>
      </div>
      <div className="col-md-6">
        {currentDogadaj ? (
          <div>
            <h4>Događaj</h4>
            <div>
              <label>
                <strong>Naziv:</strong>
              </label>{" "}
              {currentDogadaj.title}
            </div>
            <div>
              <label>
                <strong>Opis:</strong>
              </label>{" "}
              {currentDogadaj.description}
            </div>
            <div>
              <label>
                <strong>Status:</strong>
              </label>{" "}
              {currentDogadaj.published ? "Published" : "Pending"}
            </div>

            <Link
              to={"/Dogadaji/" + currentDogadaj.id}
              className="badge badge-warning"
            >
              Uredi
            </Link>
          </div>
        ) : (
          <div>
            <br />
            <p>Klikni na odaberi događaj...</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default DogadajiList;
