import React, { useState } from "react";
import DogadajDataService from "../services/Dogadajservice";

const AddDogadaj = () => {
  const initialDogadajistate = {
    id: null,
    naziv: "",
    opis: "",
    favorit: false
  };
  const [Dogadaj, setDogadaj] = useState(initialDogadajistate);
  const [submitted, setSubmitted] = useState(false);

  const handleInputChange = event => {
    const { name, value } = event.target;
    setDogadaj({ ...Dogadaj, [name]: value });
  };

  const saveDogadaj = () => {
    var data = {
      naziv: Dogadaj.naziv,
      opis: Dogadaj.opis
    };

    DogadajDataService.create(data)
      .then(response => {
        setDogadaj({
          id: response.data.id,
          naziv: response.data.naziv,
          opis: response.data.opis,
          favorit: response.data.favorit
        });
        setSubmitted(true);
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  };

  const newDogadaj = () => {
    setDogadaj(initialDogadajistate);
    setSubmitted(false);
  };

  return (
    <div className="submit-form">
      {submitted ? (
        <div>
          <h4>Događaj je uspješno dodan!</h4>
          <button className="btn btn-success" onClick={newDogadaj}>
            Dodaj
          </button>
        </div>
      ) : (
        <div>
          <div className="form-group">
            <label htmlFor="naziv">Naziv</label>
            <input
              type="text"
              className="form-control"
              id="naziv"
              required
              value={Dogadaj.naziv}
              onChange={handleInputChange}
              name="naziv"
            />
          </div>

          <div className="form-group">
            <label htmlFor="opis">Opis</label>
            <input
              type="text"
              className="form-control"
              id="opis"
              required
              value={Dogadaj.opis}
              onChange={handleInputChange}
              name="opis"
            />
          </div>

          <button onClick={saveDogadaj} className="btn btn-success">
            Potvrdi
          </button>
        </div>
      )}
    </div>
  );
};

export default AddDogadaj;
